pre,.mw-code{white-space:pre}#bodyContent a.external[href ^="http://wiki.archlinux.org"],#bodyContent a.external[href ^="https://wiki.archlinux.org"]{background:none;padding-right:0}span.archwiki-template-pkg,span.archwiki-template-man{font-family:monospace}div.archwiki-template-box{padding:5px;border:thin solid black;margin:0.5em 0;overflow:hidden}div.archwiki-template-box.archwiki-template-box-note{background-color:#ddf;border-color:#bbd}div.archwiki-template-box.archwiki-template-box-warning{background-color:#fdd;border-color:#dbb}div.archwiki-template-box.archwiki-template-box-tip{background-color:#dfd;border-color:#bdb}div.archwiki-template-message{position:relative;overflow:hidden;min-height:4.6em;margin-bottom:1em;background-color:#f9faff;border:1px solid #d7dfe3;padding:0 100px;text-align:center}div.archwiki-template-message > p > a{display:block;position:absolute;top:5px}div.archwiki-template-message > p > a:first-of-type{left:26px}div.archwiki-template-message > p > a:last-of-type{right:26px}div.archwiki-template-message > div{font-size:90%}div.archwiki-template-message ul,div.archwiki-template-message ol{text-align:left}div.archwiki-template-meta-related-articles-start{float:right;clear:right;width:25%;margin:0 0 0.5em 0.5em}div.archwiki-template-meta-related-articles-start > p{background:#333;color:white;padding:0.2em;border-bottom:5px #08c solid;margin:0;text-align:center;font-weight:bold}div.archwiki-template-meta-related-articles-start > ul{list-style-type:none;list-style-image:none;margin:0;padding:0.3em}div.archwiki-template-meta-related-articles-start > ul > li{padding:0.4em 0;line-height:1}@media screen and (min-width:721px){div.archwiki-main-page-columns{column-count:2}div.archwiki-main-page-columns > *{page-break-inside:avoid;display:inline-block;width:100%}}
